const DataInput = (props) => {
  /* list of supported file types */
  const SheetJSFT = [
    "xlsx",
    "xlsb",
    "xlsm",
    "xls",
    "xml",
    "csv",
    "txt",
    "ods",
    "fods",
    "uos",
    "sylk",
    "dif",
    "dbf",
    "prn",
    "qpw",
    "123",
    "wb*",
    "wq*",
    "html",
    "htm",
  ]
    .map(function (x) {
      return "." + x;
    })
    .join(",");

  const clickHandler = (e) => {
    setTimeout(() => {
      document.querySelectorAll("tr").forEach((e) => {
        if (e.children.length > 3 && e.firstChild.innerHTML !== "") {
          e.insertAdjacentHTML(
            "beforebegin",
            "<tr class='eventDay'><td colspan='3'>" +
              e.children[0].innerHTML +
              "</td><td class='arrow'></td></tr>"
          );
          e.children[0].remove();
        } else if (e.firstChild.innerHTML === "") {
          e.children[0].remove();
        } else {
          return;
        }
      });
    }, 5000);
  };

  setInterval(function () {
    console.log("Time left: " + getTimeLeft(timeout) + "s");
  }, 2000);

  function getTimeLeft(timeout) {
    return Math.ceil(
      (timeout._idleStart + timeout._idleTimeout - Date.now()) / 1000
    );
  }

  const handleChange = (e) => {
    const files = e.target.files;
    if (files && files[0]) props.handleFile(files[0]);
  };
  return (
    <form className="form-inline">
      <div className="form-group">
        <label htmlFor="file">Spreadsheet</label>
        <input
          onClick={clickHandler}
          type="file"
          className="form-control"
          id="file"
          accept={SheetJSFT}
          onChange={handleChange}
        />
      </div>
    </form>
  );
};

export default DataInput;
