import { useEffect, useState } from "react";

const OutTable = (props) => {
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      document.querySelectorAll("tr").forEach((e) => {
        if (e.children.length > 3 && e.firstChild.innerHTML !== "") {
          e.insertAdjacentHTML(
            "beforebegin",
            "<tr class='eventDay'><td colspan={children.length}>" +
              e.children[0].innerHTML +
              "</td><td class='arrow'></td></tr>"
          );
          e.children[0].remove();
        } else if (e.firstChild.innerHTML === "") {
          e.children[0].remove();
        } else {
          return;
        }
      });
    }, 1000);
  }, [props.data.length]);

  return (
    <div className="table-responsive">
      <table className="table table-striped">
        <tbody>
          {props.data.map((r, i) => (
            <tr key={i}>
              {props.cols.map((c) => (
                <td className="td" key={c.key}>
                  {r[c.key]}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OutTable;
