import { useEffect, useState } from "react";

const OutTable = (props) => {
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      document.querySelectorAll("tr").forEach((e) => {
        if (e.children.length > 3 && e.firstChild.innerHTML !== "") {
          e.insertAdjacentHTML(
            "beforebegin",
            `<tr class='eventDay'><td colspan=${e.children.length}>${e.children[0].innerHTML}</td><td class='arrow'></td></tr>`
          );
          e.children[0].remove();
          e.style.display = "none";
        } else if (e.firstChild.innerHTML === "") {
          e.children[0].remove();
        } else if (e.children.length > 2) {
          console.log(e.children.length);
          e.style.display = "none";
        }
      });

      document.querySelector("table").setAttribute("id", "schedule-table");

      document
        .querySelector("table")
        .querySelectorAll(".arrow")
        .forEach((arrow) =>
          arrow.addEventListener("click", (event) => {
            let ARROW = "arrowOn";
            let element = event.target;
            console.log(element);
            let elementParent = element.parentNode.parentNode;
            let elementChildren = elementParent.children;
            element.classList.toggle(ARROW);
            for (let elem of elementChildren) {
              if (element.children.length > 2) {
                elem.style.display = "none";
              }
            }
            if (element.classList.value === "arrow arrowOn") {
              for (let elem of elementChildren) {
                if ((element.style.display = "none;")) {
                  elem.style.display = "table-row";
                }
              }
            }
          })
        );
    }, 1000);
  }, [props.data.length]);

  return (
    <div className="table-responsive">
      <table className="table table-striped">
        <tbody>
          {props.data.map((r, i) => (
            <tr key={i}>
              {props.cols.map((c) => (
                <td className="td" key={c.key}>
                  {r[c.key]}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OutTable;
