import { useEffect, useState } from "react";

const OutTable = (props) => {
  const [table, setTable] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      document.querySelectorAll("tr").forEach((e) => {
        if (e.children.length > 3 && e.firstChild.innerHTML !== "") {
          e.insertAdjacentHTML(
            "beforebegin",
            "<tr class='eventDay'><td colspan='3'>" +
              e.children[0].innerHTML +
              "</td><td class='arrow'></td></tr>"
          );
          e.children[0].remove();
        } else if (e.firstChild.innerHTML === "") {
          e.children[0].remove();
        } else {
          return;
        }
      });
      console.log("in Timeout");
    }, 3000);
  }, [props.data.length]);

  // while (props.data.length < 0) {
  //   return <div>Loading...</div>;
  // }

  return (
    <div className="table-responsive">
      <table className="table table-striped">
        {/* <thead>
          <tr>
            {props.cols.map((c) => (
              <th key={c.key}>{c.name}</th>
            ))}
          </tr>
        </thead> */}
        {/* {props.data
          .filter((r, i) => {
            return props.data[i][0] !== undefined;
          })
          .map((r, i) => (
            <tbody key={i}>
              <tr>
                {props.cols.map((c) => (
                  <td key={c.key}>{r[c.key]}</td>
                ))}
              </tr>
            </tbody>
          ))} */}
        <tbody>
          {props.data.map((r, i) => (
            <tr key={i}>
              {props.cols.map((c) => (
                <td className="td" key={c.key}>
                  {r[c.key]}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default OutTable;
